package myPro;

import java.util.Scanner;

public class unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =5;
		System.out.println(a++);
		System.out.println(a);
		a++;
		System.out.println(a);
		System.out.println(++a);
		
		System.out.println("  **  ");
		
		System.out.println(a--);
		System.out.println(a);
		a--;
		System.out.println(a);
		System.out.println(--a);
		
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
	

		
		


	}

}
