package myPro;
import java.util.*;
public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in=new Scanner(System.in);
		System.out.println("Enter the Limit: ");
		int n = in.nextInt();
		int i=2;
		
		while(i<=n) {
			System.out.println(i);
			i++;
			
		}
	}

}
